import React, {Component} from "react";

import Card from "../card/Card";
import Axios from "axios";
import { SERVER_URL } from "../consts";

class Board extends Component {


	constructor(props) {
		super(props);
		this.state = {
			errsor: null,
			isLoaded: false,
			champions: [],
		
		};

	}


	

	
	// randomPick(champs) {
	// 	let iChamps = [];
	// 	for (let i = 0; i < 120 ; i++) {
			
	// 			console.log()
	// 		iChamps.push({"id": this.state.champions.key, "name": this.state.name, "img":  this.state.key + "_0.jpg" 
	// 		 ,"momo" : this.state.champions[i] });
	// 			console.log(iChamps[i]);
	// 		}
	// 		return iChamps;
	// }
	// , "attack":elem.info.attack , "defense":elem.defense  pour le randompick
		
	
	randomPick(champs) {
		let iChamps = [];
		for (let i = 0; i < 120 ; i++) {
			
				
			iChamps.push({"id": this.state.key[i], "name": this.state.name, "img":  this.state.key + "_0.jpg" 
			 ,"momo" : this.state.champions[i] });
				console.log(iChamps[i]);
			}
			return iChamps;
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
	deckcards(champs) {

		let cards = [];
		for (let i = 0; i < 20; i++) {
			cards.push(
				<Card id={champs[i].id}
					  name={champs[i].name}
					  img={champs[i].img}
					  attack={champs[i].attack}
					  defense={champs[i].defense}
					  key={i}
					  />);
		}
		return cards;
	}



	


	
	componentDidMount() {
		// fetch("champion.json")
		// 	.then(res => res.json())
		Axios
			.get(SERVER_URL + "/cards/getAll")

			
			.then(
				(result) => {
					console.log(result)
					if(result.data.status=== "ok"){
						console.log(result)
					let champions = this.randomPick(result);
					
					this.setState({
						isLoaded: true,
						champions: champions
					});
				}},
				(error) => {
					this.setState({
						isLoaded: true,
						error
					});
				}
				);

	}


	render() {

		const {error, isLoaded, champions} = this.state;

		if (error) {
			return <div>Error: {error.message}</div>;
		} else if (!isLoaded) {
			return <div>Loading...</div>;
		} else {
			let cards = this.deckcards(champions);
			return (
				<section className="row" classID="board">
					{cards}
				</section>
			);
		}
	}
}

export default Board;
