                    <Link to="/accueil">Accueil</Link>
                    <Link to="/profile">Mon profil</Link>
                    <button onClick={this.handleDisconnection}>Deconnexion</button>  
            </nav> */}
            <header>
        
        <nav className="navbar navbar-expand-lg navbar-light bg-light navbar-dark bg-dark menu">
            <a className="navbar-brand" >
            <Link to="/Game">Accueil</Link>
            </a>
            <a className="nav-item nav-link active"> 
            <Link to="/Profile">Profil</Link>

            </a>
              
              
              <a className="nav-item nav-link" >
              <Link to="/Réglesdejeux">Régles de jeux</Link>
              </a> 
              <button onClick={this.handleDisconnection}>Deconnexion</button> 
              <a className="nav-item nav-link" onClick={this.handleDisconnection} >Deconnexion</a>
        </nav>
    </header>
            <h1>Mon Profil </h1>
            <div>
                <h2>Mon pseudo : {this.state.pseudo} </h2>
                <h2>Mon mail : {this.state.email} </h2>
            </div>
        </body>  
    );

//}
    }
}

export default Profile;
