import React, {Component} from "react";

import Card from "../card/Card";
import Axios from "axios";
import { SERVER_URL } from "../consts";

class Board extends Component {


	constructor(props) {
		super(props);
		this.state = {
			errsor: null,
			isLoaded: false,
			champions: [],
		
		};

	}


	

	
	// randomPick(champs) {
	// 	let rChamps = [];
	// 	for (let i = 0; i < 120 ; i++) {

	// 		let elem = champs.splice(Math.floor(Math.random() * champs.length), 1)[0];

	// 		rChamps.push({"id": elem.key, "name": elem.name, "img": elem.key + "_0.jpg", "attack":elem.info.attack , "defense":elem.info.defense });
	// 	}

	// 	return rChamps;
	// }

	deckcards(champs) {
		let cards = [];
		for (let i = 0; i < 20; i++) {
			cards.push(
				<Card id={champs[i].id}
					  name={champs[i].name}
					  img={champs[i].img}
					  attack={champs[i].attack}
					  defense={champs[i].defense}
					  key={i}
					  
				/>);
		}
		return cards;
	}


	

	


	
	componentDidMount() {
		Axios
			.get(
				SERVER_URL +
				"/cards/getAll"
			)
			.then(res => res.json())
			
			.then(
				(result) => {
					// let rChamps = this.randomPick(result);
					
					this.setState({
						isLoaded: true,
						result: result
					});
				},
				(error) => {
					this.setState({
						isLoaded: true,
						error
					});
				}
			);

	}


	render() {

		const {error, isLoaded, result} = this.state;

		if (error) {
			return <div>Error: {error.message}</div>;
		} else if (!isLoaded) {
			return <div>Loading...</div>;
		} else {
			let cards = this.deckcards(result);
			return (
				<section className="row" classID="board">
					{cards}
				</section>
			);
		}
	}
}

export default Board;
